import React from 'react';
import '../../App.css';
import { useState } from 'react';
import VehicleCard from '../VehicleCard';
export default function SignUp(){

  function handleChange(event) {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  }

  const [formData, setFormData] = useState({
    title: "",
    description: "",
    image: "",
  })

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/vehicles', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(
        formData
      ),
    }).then((response) => response.json())
      .then((newPost) => {
        setFormData(newPost)
      });
  }
  return (
    <div >
      <h1 className='sign-up'>Below is a snippet from our Showroom.</h1>
      <div className='iframe'>
        <iframe width="420" height="315"
             src="https://www.youtube.com/embed/VWrJkx6O0L8">
        </iframe>
      </div>
      <h1 className='signup'>If you'd like your vehicle to be submitted to our Showroom, kindly fill the form below to send details. </h1>
      <form className="form" onSubmit={handleSubmit} >
        <label for="fname">name of vehicle:</label>
        <input className="inputs" name='title' onChange={handleChange} type="text" placeholder="name of your vehicle" />
        <label for="fname">image url:</label>
        <input className="inputs" name='image' onChange={handleChange}  type="text" placeholder="input vehicle image url" />
        <label for="fname">vehicle description:</label>
        <input className="inputs" name='description' onChange={handleChange} type="text" placeholder="describe your vehicle"/>
        <div className="d-grid gap-2">
          <button className="btn btn-primary" >SEND</button>
        </div>
      </form>

    </div>




  )
}
